function _EVENT_()
{
	
}

/**
 * 事件公用类
 * 
 */
var MyouEvent = {

		/**
		 * 获取事件对象 用于修复浏览器的兼容性
		 */
		getE : function( e )
		{
			return e || window.event;
		}
		,
		/**
		 * 是否是基于ＩＥ的事件类型
		 */
		isIEEvent : function( )
		{
			return window.event == undefined ? false : true;
		}
		,
		/**
		 * 取消事件的默认行为
		 */
		preventDefault : function( e )
		{
			if (this.isIEEvent())
			{
				e.returnValue = false;
			} else if (e.preventDefault)
			{
				e.preventDefault();
			}
			return this;
		}
		,
		/**
		 * 取消事件的传播
		 */
		stopPropagation : function( e )
		{
			if ( this.isIEEvent() )
			{
				e.cancelBubble = true;
				return false;
			} else if (e.stopPropagation)
			{
				e.stopPropagation();
			}
			return this;
		}
};